let labels = [];
let planned = [];
let executed = [];

let battery = 2.5;
const capacity = 5.0;
const chargeRate = 5.0;
const dischargeRate = 5.0 / 3;
const lookAhead = 2;

function simulateLoad(hour, season) {
    if (season === 'winter') {
        if (hour >= 6 && hour < 9) return 0.25 + 0.35 * Math.sin((hour - 6)/3);
        if (hour >= 16 && hour < 20) return 0.6 + 0.5 * Math.sin((hour - 16)/4);
        return 0.4;
    }
    if (season === 'summer') {
        if (hour >= 12 && hour < 16) return 0.8 + 1.2 * Math.sin((hour - 12)/4);
        return 0.4;
    }
    if (season === 'spring' || season === 'autumn') {
        if (hour >= 7 && hour < 10) return 0.4 + 0.4 * Math.sin((hour - 7)/3);
        if (hour >= 17 && hour < 21) return 0.6 + 0.6 * Math.sin((hour - 17)/4);
        return 0.35;
    }
    return 0.4;
}

let data = msg.payload;
let avg = msg.averagePrice;
let now = new Date();
let month = now.getMonth() + 1;
let season = (month <= 2 || month === 12) ? "winter" : (month <= 5) ? "spring" : (month <= 8) ? "summer" : "autumn";

data.forEach((row, idx, arr) => {
    let label = row.Date + " " + row.Time;
    labels.push(label);

    let price = row.price_no_tax;
    let solar = row.solar_production || 0;
    let usage = simulateLoad(parseInt(row.Time.split(":")[0]), season);

    // Look ahead to find any low price coming
    let upcomingPrices = arr.slice(idx+1, idx+1+lookAhead).map(r => r.price_no_tax);
    let lowComing = upcomingPrices.some(p => p !== null && p < 0.03);

    let action = 0; // 1 = charge/solar, -1 = discharge, 0 = grid
    let executedAction = 0;
    let shelly = false;

    // Pre-discharge if low price coming
    if (lowComing && battery > 0) {
        let discharge = Math.min(dischargeRate, battery);
        battery -= discharge;
        action = -1;
    }

    if (price < 0.03) {
        let charge = Math.min(chargeRate, capacity - battery);
        battery += charge;
        action = 1;
        shelly = true;
    } else if (solar > usage) {
        let charge = Math.min(chargeRate, capacity - battery);
        battery += charge;
        action = 1;
        shelly = true;
    } else if (price > avg * 1.2 && battery > 0) {
        let discharge = Math.min(dischargeRate, battery);
        battery -= discharge;
        action = -1;
    } else {
        action = 0;
    }

    battery = Math.max(0, Math.min(battery, capacity));
    executedAction = (solar > usage) ? 1 : (price > avg * 1.2 ? -1 : 0);

    planned.push(action);
    executed.push(executedAction);
});

msg.preplan = {
    payload: [{
        series: ["Planned"],
        data: [planned],
        labels: labels
    }]
};

msg.execution = {
    payload: [{
        series: ["Executed"],
        data: [executed],
        labels: labels
    }]
};

return [msg.preplan, msg.execution];

