let consumption = [],
    solar = [],
    cost = [],
    unit_price = [],
    negative_price = [],
    weather_values = [],
    weather_text = [],
    weather_colors = [];

msg.payload.forEach(row => {
    const timestamp = new Date(`${row.Date}T${row.Time}:00`).toISOString();

    consumption.push({ x: timestamp, y: row['Consumption[kWh]'] });
    solar.push({ x: timestamp, y: row['Solar_Production_Estimate[kW]'] });
    cost.push({ x: timestamp, y: row['Consumption_Cost[EUR]'] });
    unit_price.push({ x: timestamp, y: row['Consumption_UnitPrice[EUR/kWh]'] });
    negative_price.push({ x: timestamp, y: row['Negative_prices[EUR/kWh]'] });

    // Map weather condition to a numeric value
    let value;
    switch (row['Weather_condition']) {
        case 'Clear sky': value = 1; break;
        case 'Mainly clear': value = 2; break;
        case 'Partly cloudy': value = 3; break;
        case 'Cloudy': value = 4; break;
        case 'Overcast': value = 5; break;
        case 'Rainy': value = 6; break;
        default: value = 0;
    }
    weather_values.push({ x: timestamp, y: value });
    weather_text.push({ x: timestamp, y: row['Weather_condition'] });

    // Map weather to color for custom use
    let color;
    switch (row['Weather_condition']) {
        case 'Clear sky': color = 'green'; break;
        case 'Partly cloudy': color = 'yellow'; break;
        case 'Cloudy': color = 'gray'; break;
        case 'Rainy': color = 'blue'; break;
        case 'Mainly clear': color = 'lightgreen'; break;
        default: color = 'lightgray';
    }
    weather_colors.push({ x: timestamp, y: color });
});

// Each output goes to a different chart input
return [
  { topic: "Consumption", payload: consumption },
  { topic: "Solar Production", payload: solar },
  { topic: "Cost", payload: cost },
  { topic: "Unit Price", payload: unit_price },
  { topic: "Negative Price", payload: negative_price },
  { topic: "Weather Condition (Numeric)", payload: weather_values },
  { topic: "Weather Condition (Text)", payload: weather_text }
];

