let rows = msg.payload;
let avgPrice = rows.reduce((acc, r) => acc + r.price_no_tax, 0) / rows.length;
msg.payload = rows;
msg.averagePrice = avgPrice;
return msg;

let now = new Date();
let match = msg.payload.find(row => {
    let t = new Date(row.Date + 'T' + row.Time + ':00');
    return t <= now && now < new Date(t.getTime() + 15 * 60000);
});
if (match && match.solar_production > match.solar_consumption) {
    msg.payload = "Surplus: Charging grid.";
} else {
    msg.payload = "";
}
return msg;

let now = new Date();
let current = msg.payload.find(r => {
    let t = new Date(r.Date + 'T' + r.Time + ':00');
    return t <= now && now < new Date(t.getTime() + 15 * 60000);
});

if (!current) return null;
let price = current.price_no_tax;
let avg = msg.averagePrice;
let shellyMsg = {};

if (price < 0 || price < avg * 0.8) {
    shellyMsg.payload = { turn: "on" }; // charging
} else if (price > avg * 1.2) {
    shellyMsg.payload = { turn: "off" }; // discharging
} else {
    shellyMsg.payload = { turn: "on" };
}

shellyMsg.url = "http://192.168.2.102/relay/0";
return shellyMsg;

let labels = [];
let planned = [];
let executed = [];

let data = msg.payload;
let avg = msg.averagePrice;

data.forEach((row) => {
    let label = row.Date + " " + row.Time;
    labels.push(label);

    let price = row.price_no_tax;
    let solar = row.solar_production;
    let usage = row.solar_consumption;

    planned.push(price < 0 || price < avg * 0.8 ? 1 : price > avg * 1.2 ? -1 : 0);
    executed.push(solar > usage ? 1 : price > avg * 1.2 ? -1 : 0);
});

msg.preplan = {
    payload: [{
        series: ["Planned"],
        data: [planned],
        labels: labels
    }]
};

msg.execution = {
    payload: [{
        series: ["Executed"],
        data: [executed],
        labels: labels
    }]
};

return [msg.preplan, msg.execution];

const now = new Date();
let start = new Date();

// Set the start time to 7 days ago (you can adjust this dynamically if needed)
start.setDate(now.getDate() - 7);

// Format for SQLite 'YYYY-MM-DD'
const startTime = start.toISOString().slice(0, 10);
const endTime = now.toISOString().slice(0, 10);

// Generate SQL query using the Date column
msg.topic = `SELECT * FROM data WHERE Date BETWEEN '${startTime}' AND '${endTime}'`;

return msg;

