const user = global.get("user")

const page = msg.name
global.set("currentPage", page);

const allowSignUp = global.get("allowSignUp")

msg.payload = {}

if (!allowSignUp && page == 'Sign Up') msg.payload = "Login"

if (!['Login', ...(allowSignUp ? ['Sign Up'] : [])].includes(page)) {
    if (!user) msg.payload = "Login"
} else{
    if (user) msg.payload = "Dashboard"
}


return msg;

