let labels = [], consumption = [], solar = [], cost = [], unit_price = [], weather = [];

msg.payload.forEach(row => {
    const label = row.Date + ' ' + row.Time;      //  Combines Date + Time columns
    labels.push(label);

    consumption.push(row['Consumption[kWh]']);    //  Matches column names exactly
    solar.push(row['Solar_Production_Estimate[kW]']);
    cost.push(row['Consumption_Cost[EUR]']);
    unit_price.push(row['Consumption_UnitPrice[EUR/kWh]']);
    weather.push(row['Weather condition']);
});

return [
    { payload: [{ series: ['Consumption'], data: [consumption], labels }] },
    { payload: [{ series: ['Solar Production'], data: [solar], labels }] },
    { payload: [{ series: ['Cost'], data: [cost], labels }] },
    { payload: [{ series: ['Unit Price'], data: [unit_price], labels }] },
    { payload: [{ series: ['Weather'], data: [weather], labels }] }
];

// Ensure date values from payload are properly formatted
const startDate = new Date(msg.payload.start);
const endDate = new Date(msg.payload.end);

// Check if dates are valid
if (isNaN(startDate) || isNaN(endDate)) {
    node.error("Invalid date selected.");
    return null;
}

// Set start time to 00:00:00 and end time to 23:59:59 for selected dates
startDate.setHours(0, 0, 0, 0);
endDate.setHours(23, 59, 59, 999);

const startTime = startDate.toISOString().slice(0, 19);
const endTime = endDate.toISOString().slice(0, 19);

msg.topic = `
SELECT * FROM data 
WHERE datetime(Date || 'T' || Time || ':00' || Timezone)
BETWEEN datetime('${startTime}') AND datetime('${endTime}')
`;

return msg;
